	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2786,1433,'java.nio.file.attribute.<strong>BasicFileAttributes<br /><br /><br /></strong><em>FileTime</em> <strong>creationTime</strong>()<br /><br /><em>Object &nbsp;&nbsp;&nbsp;</em> <strong>fileKey</strong>()<br />Returns an object that uniquely identifies the given file, or null if a file key is not available.<br /><br /><em>boolean &nbsp;&nbsp;&nbsp;</em> <strong>isDirectory</strong>()<br /><br /><em>boolean &nbsp;&nbsp;&nbsp;</em> <strong>isOther</strong>()<br />Tells whether the file is something other than a regular file, directory, or symbolic <em>link.</em><br /><br /><em>boolean</em> &nbsp;&nbsp;&nbsp; <strong>isRegularFile</strong>()<br /><br /><em>boolean &nbsp;&nbsp;&nbsp;</em> <strong>isSymbolicLink</strong>()<br /><br /><em>FileTime &nbsp;&nbsp;&nbsp;</em> <strong>lastAccessTime</strong>()<br /><br /><em>FileTime &nbsp;&nbsp;&nbsp;</em> <strong>lastModifiedTime</strong>()<br /><br /><em>long &nbsp;&nbsp;&nbsp;</em> <strong>size</strong>()',true,'');
