	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2788,1435,'java.nio.file.attribute.<strong>BasicFileAttributeView - </strong>основные атрибуты<br />java.nio.file.attribute.<strong>DosFileAttributeView - </strong>атрибуты DOS/Windows<strong><br /></strong>java.nio.file.attribute.<strong>PosixFileAttributeView - </strong>атрибуты POSIX<br /><br />Последние два наследуют от первого.<br /><br />Пример получения:<br /><br />Path path = ...<br />BasicFileAttributeView view = Files.getFileAttributeView(path, BasicFileAttributeView.class);<br />if (view != null) {<br />&nbsp;&nbsp;&nbsp; BasicFileAttributes attrs = view.readAttributes();<br />&nbsp;&nbsp;&nbsp; view.setTimes(lastModifiedTime, lastAccessTime, createTime);<br />}<br />',true,'');
