	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2927,1484,'Для выполнения непосредственно SQL. По умолчанию работает в режиме <em>auto-commit. </em>Можно выполнить метод <strong>setAutoCommit</strong>(<em>false</em>), тогда после каждого изменения придется вызывать метод <strong>commit</strong>(), чтобы зафиксировать изменения, либо <strong>rollback</strong>(), чтобы отменить все незафиксированное',true,'');
