	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2937,1496,'Map&lt;Integer, String&gt; idToNameMap = new HashMap&lt;&gt;();<br />ResultSet rs = stmt.executeQuery("select id, name from species");<br /><strong>while</strong>(rs.next()) {<br />&nbsp;&nbsp;&nbsp; int id = rs.getInt("id");<br />&nbsp;&nbsp;&nbsp; String name = rs.getString("name");<br />&nbsp;&nbsp;&nbsp; idToNameMap.put(id, name);<br />}<br /><br />ResultSet rs = stmt.executeQuery("select count(*) from animal");<br /><strong>if</strong>(rs.next())<br />&nbsp;&nbsp;&nbsp; System.out.println(rs.getInt(1));<br /><br />&nbsp;&nbsp;&nbsp; или<br />&nbsp;&nbsp;&nbsp; <br />ResultSet rs = stmt.executeQuery("select count(*) as count from animal");<br /><strong>if</strong>(rs.next())<br />&nbsp;&nbsp;&nbsp; System.out.println(rs.getInt("count"));&nbsp;&nbsp;&nbsp;',true,'');
