INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1500,8,0,'Особые геттеры класса <strong>ResultSet</strong>.<br /><br /><em>Date </em><strong>getDate</strong>(<em>int</em> columnIndex, <em>Calendar</em> cal) throws SQLException<br /><em>Date </em><strong>getDate</strong>(<em>String</em> columnLabel, <em>Calendar</em> cal) throws <em>SQLException</em><br /><em>Time</em> <strong>getTime</strong>(<em>int</em> columnIndex, <em>Calendar</em> cal) throws <em>SQLException</em><br /><em>Time </em><strong>getTime</strong>(<em>String</em> columnLabel, <em>Calendar</em> cal) throws <em>SQLException</em><br /><em>Timestamp </em><strong>getTimestamp</strong>(<em>int</em> columnIndex, <em>Calendar</em> cal) throws <em>SQLException</em><br /><em>Timestamp </em><strong>getTimestamp</strong>(<em>String</em> columnLabel, <em>Calendar</em> cal) throws SQLException<br /><br /><strong>Calendar </strong>используется для конструирования миллисекунд при работе с БД, не хранящей timezone<br /><br /><br />&lt;T&gt; <em>T </em><strong>getObject</strong>(<em>int</em> columnIndex, <em>Class</em>&lt;T&gt; type) throws <em>SQLException</em><br /><br />Приводит полученный оъект к указанному типу.<br />Может не поддерживаться JDBC-драйвером.<br /><em>SQLException</em>, если type == null или приведение невозможно.<br /><br /><em>Object </em><strong>getObject</strong>(<em>String</em> columnLabel, <em>Map</em>&lt;String,Class&lt;?&gt;&gt; map) throws <em>SQLException</em><br /><br />не очень понял из javadoc',TIMESTAMP '2019-03-16 17:53:18',TIMESTAMP '2019-03-16 17:54:40');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (1501,8,0,'Метод <strong>getDate</strong>() класса <strong>ResultSet</strong> возвращает java.sql.<strong>Date</strong> с компонентом только даты. Его можно конвертировать в <strong>LocalDate</strong> методом <strong>toLocalDate()</strong>.<br /><br />Абсолютно схожее поведение имеется у <strong>getTime</strong>() и <strong>getTimestamp</strong>().<br />(методы конвертации <strong>toLocalTime</strong>() и <strong>toLocalDateTime</strong>(), соответственно).<br />',TIMESTAMP '2019-03-16 20:43:27',TIMESTAMP '2019-03-16 20:43:27');
