	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (2941,1502,'Метод может вернуть любой тип. Для простых типов вернет обертку примитива. Для varchar - строку. В реальной жизни используется редко.',true,'');
