INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2311,13,0,'SQL - декларативный язык. Это означает, что мы описываем то, <em>что</em> хотим получить, а не то, <em>как</em> мы это делаем. Поэтому стандартный SQL не имеет управляющих конструкций типа ветвления, циклов и т.п.',TIMESTAMP '2019-04-20 01:28:37',TIMESTAMP '2019-04-20 01:28:37');
