	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (6400,2388,'Мое собственное наблюдение!<br /><br />1) <strong>Статические методы</strong>: методы-ссылки получаются четко по сигнатуре.<br />2) <strong>Методы экземпляра с известным экземпляром</strong>: методы-ссылки получаются четко по сигнатуре, метод вызывается на экземпляре.<br />3) <strong>Методы экземпляра с неизвестным экземпляром</strong>: экземпляр становится первым параметром, последующие параметры - из сигнатуры, метод вызывается на первом параметре',true,'');
