INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2553,13,0,'Некоторые интересные свойства NULL.<br /><br />- могут появляться в любой колонке не имеющей ограничений NOT NULL или PRIMARY KEY<br />- не равны и не неравны друг другу. Однако DISTINCT считает их дубликатами.<br />- при сортировке возглавляют или замыкают выборку (зависит от СУБД)<br />- (12*NULL)/4 = NULL<br />- агрегирующие функции SUM(), AVG(), MAX() игнорируют NULL, в отличие от COUNT(*)<br />- в GROUP BY создают единую группу',TIMESTAMP '2019-05-11 01:56:18',TIMESTAMP '2019-05-11 01:56:18');
