INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2657,13,2,'Что такое "производная колонка" (<em>derived column</em>)?',TIMESTAMP '2019-05-19 08:23:08',TIMESTAMP '2019-05-19 08:23:08');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7123,2657,'Это результат в SELECT, полученный путем применения операторов и функций к другим колонкам и/или константам. Примеры:<br /><br />SELECT 2 + 3;<br /><br />SELECT title_id, price, 0.10 AS "Discount", price * (1 - 0.10) AS "New price"',true,'');
