INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2659,13,0,'Арифметические операции:<br /><br />-expr<br />+expr<br />expr1 + expr2<br />expr1 &ndash; expr2<br />expr1 * expr2<br />expr1 / expr2',TIMESTAMP '2019-05-19 08:30:32',TIMESTAMP '2019-05-19 08:30:32');
