INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2661,13,0,'При применении арифметических операций к операндам разных числовых типов, производится неявное приведение к единому типу.<br /><br />Кроме того, операция над операндами одного типа не обязана выдавать результат того же типа. Например, произведение двух SMALLINT может дать INTEGER',TIMESTAMP '2019-05-19 08:35:10',TIMESTAMP '2019-05-19 08:36:50');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2662,13,2,'Укажите приоритет арифметических операторов и логических связок.',TIMESTAMP '2019-05-19 08:42:22',TIMESTAMP '2019-05-19 08:42:22');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7124,2662,'+, - (унарные)<br />*, / <br />+, - <br />=, &lt;&gt;, &lt;, &lt;=, &gt;, &gt;= <br />NOT<br />AND<br />OR',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2663,13,2,'Опишите оператор конкатенации строк и ее важные свойства',TIMESTAMP '2019-05-19 08:50:59',TIMESTAMP '2019-05-19 09:53:06');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7125,2663,'''a'' || ''b''<br /><br />- можно использовать в цепочке:<br />''a'' || ''b'' || ''c'' || ''d'' ==&gt; ''abcd''<br /><br />- можно использовать везде в SELECT, WHERE и ORDER BY<br /><br />- Если один из операндов NULL, то результат будет NULL<br /><br />- В TSQL конкатенация&nbsp; +<br /><br />- в MySQL конкатенация - CONCAT() (там же и кастинг до строки происходит)<br /><br />- В Oracle '''' интерпретируется как NULL, поэтому ''a'' || NULL || ''b'' ==&gt; ''ab''<br /><br />- Oracle и DB2 также поддерживают CONCAT<br /><br />- Oracle, MySQL, Postgres осуществляют кастинг к строке при конкатенации',true,'');
