	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7126,2664,'SELECT au_fname || '' '' || au_lname AS "Author name"<br />&nbsp;&nbsp;&nbsp; FROM authors<br />&nbsp;&nbsp;&nbsp; ORDER BY au_lname ASC, au_fname ASC;',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2665,13,2,'Выведите продажи заголовков-биографий, сконкатенированные с заголовком.',TIMESTAMP '2019-05-19 09:01:38',TIMESTAMP '2019-05-19 09:02:21');
