INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2666,13,2,'Выведите первые буквы заголовков',TIMESTAMP '2019-05-19 10:11:59',TIMESTAMP '2019-05-19 10:11:59');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7128,2666,'SELECT SUBSTRING(title_name FROM 1 FOR 1) <br />&nbsp;&nbsp;&nbsp; FROM titles',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2667,13,2,'Опишите функцию SUBSTRING() и ее свойства',TIMESTAMP '2019-05-19 10:16:09',TIMESTAMP '2019-05-19 10:16:09');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7129,2667,'SUBSTRING(<em>string</em> FROM <em>start</em> [FOR <em>length</em>])<br /><br />- подстрока пустой строки - пустая строка<br /><br />- индекс начинается с 1<br /><br />- можно использовать в SELECT, WHERE, ORDER BY<br /><br />- в TSQL синтаксис: SUBSTRING(string, start, length)<br /><br />- в Oracle и DB2 синтаксис: SUBSTR(string, start [,length])<br /><br />- MySQL и Postgres тоже поддерживают SUBSTR(string, start, length)<br /><br />- Oracle трактует пустые строки как NULL, поэтому SUBSTR(NULL, 1, 2) == ''''',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2668,13,2,'Опишите функции изменения регистра и их свойства.',TIMESTAMP '2019-05-19 10:47:44',TIMESTAMP '2019-05-19 10:47:44');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7130,2668,'UPPER(), LOWER()<br /><br />- возвращают NULL, если аргумент NULL<br /><br />- зачастую используются для безрегистрового сравнения в тех СУБД, где операции со строками чувствительны к регистру.<br /><br />- могут быть использованы в SELECT, WHERE, ORDER BY<br /><br />- В Oracle UPPER(NULL)/LOWER(NULL) вернет ''''',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2669,13,2,'Удалите буквы ''H'' из фамилий авторов с помощью тримминга.',TIMESTAMP '2019-05-19 11:00:35',TIMESTAMP '2019-05-19 11:00:35');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7131,2669,'SELECT au_lname, TRIM(LEADING ''H'' FROM au_lname)<br />&nbsp;&nbsp;&nbsp; FROM authors;',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2670,13,2,'Опишите операцию тримминга строк и ее свойства.',TIMESTAMP '2019-05-19 11:11:32',TIMESTAMP '2019-05-19 11:11:32');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7132,2670,'TRIM([[LEADING | TRAILING | BOTH] FROM] <em>string</em>)<br /><br />TRIM([LEADING | TRAILING | BOTH] ''trim_chars'' FROM string) - удаление произвольных символов<br /><br />BOTH - по умолчанию<br /><br />- полезно для обработки строк типа CHAR (у которых фиксированная длина и могут быть хвостовые пробелы)<br /><br />- если аргумент NULL, то вернется NULL<br /><br />- можно использовать в SELECT, WHERE, ORDER BY<br /><br />- в TSQL нет TRIM, но есть LTRIM() и RTRIM(). Удаление произвольного символа не предусмотрено.<br /><br />- В Oracle и DB2 в <em>trim_chars</em> запрещены множественные символы<br /><br />&nbsp;- В Oracle TRIM(NULL) вернет ''''',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2671,13,2,'Выведите список книг (titles), заголовки которых короче 30 символов, отсортированные по возрастанию длины.',TIMESTAMP '2019-05-19 11:14:25',TIMESTAMP '2019-05-19 11:14:25');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7133,2671,'SELECT title_name, CHARACTER_LENGTH(title_name) AS "Len"<br />&nbsp;&nbsp;&nbsp; FROM titles<br />&nbsp;&nbsp;&nbsp; WHERE CHARACTER_LENGTH(title_name) &lt; 30<br />&nbsp;&nbsp;&nbsp; ORDER BY CHARACTER_LENGTH(title_name) ASC;',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2672,13,2,'Опишите функцию поиска длины строки и ее свойства',TIMESTAMP '2019-05-19 11:18:51',TIMESTAMP '2019-05-19 11:18:51');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7134,2672,'CHARACTER_LENGTH(<em>string</em>)<br /><br />- имеет синоним CHAR_LENGTH<br /><br />- с аргументом NULL вернет NULL<br /><br />- можно использовать в SELECT, WHERE, ORDER BY<br /><br />- В SQL также описаны функции BIT_LENGTH() и OCTET_LENGTH(), возвращающие длину в битах и байтах.<br /><br />- В TSQL функция называется LEN<br /><br />- В Oracle и DB2 функция называется LENGTH<br /><br />- В Oracle LENGTH('''') вернет NULL',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2673,13,2,'Выведите индекс вхождения буквы ''e'' в именах авторов',TIMESTAMP '2019-05-19 11:25:41',TIMESTAMP '2019-05-19 11:25:41');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7135,2673,'SELECT au_fname, POSITION(''e'' IN au_fname) AS "Pos e"<br />&nbsp;&nbsp;&nbsp; FROM authors;',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2674,13,2,'Опишите функцию вхождения подстроки и ее свойства.',TIMESTAMP '2019-05-19 11:32:40',TIMESTAMP '2019-05-19 11:32:40');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7136,2674,'POSITION(<em>substring</em> IN <em>string</em>)<br /><br />- вернет 0, если вхождения нет, а также если <em>string</em> - ''''<br /><br />- Вернет NULL, если <em>string</em> - NULL<br /><br />- может использоваться в SELECT, WHERE, ORDER BY<br /><br />- В TSQL используется функция CHARINDEX(substring, string)<br /><br />- В Oracle используется функция INSTR(string, substring)<br /><br />- В DB2 используется функция POSSTR(string, substring)<br /><br />- В Oracle INSTR('''', substring) вернет NULL',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2675,13,0,'А еще в SQL есть функция OVERLAY(), осуществляющая замену:<br /><br />OVERLAY(''Txxxxas'' PLACING ''hom'' FROM 2 FOR 4) is ''Thomas''',TIMESTAMP '2019-05-19 11:34:11',TIMESTAMP '2019-05-19 11:34:11');
