INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2678,13,0,'В различных СУБД существует чрезвычайно обширный API для работы с датой/временем',TIMESTAMP '2019-05-19 12:29:32',TIMESTAMP '2019-05-19 12:29:32');
