	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7142,2682,'CAST(expr AS data_type)<br /><br />- кастинг к строковому типу всегда возможен<br /><br />- кастинг строки возможен к любому типу, если строка представляет собой валидный литерал<br /><br />- при конверсии DATE&nbsp; в TIMESTAMP время будет 00:00:00<br /><br />- для NULL-аргумента CAST вернет NULL<br /><br />- способ округления зависит от СУБД<br /><br />- может использоваться в SELECT, FROM, WHERE<br /><br />- в Oracle CAST('''' AS CHAR) вернет NULL',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2683,13,2,'Выведите цену книг, причем для "истории" и "психологии" цена должна быть выше на 10 и 20 % соответственно.',TIMESTAMP '2019-05-19 14:18:35',TIMESTAMP '2019-05-19 14:18:35');
