INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2746,16,2,'Как запустить поток?',TIMESTAMP '2019-05-22 16:55:46',TIMESTAMP '2019-05-24 17:51:35');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7321,2746,'1) инстанциируем объект потока<br /><br />MyThread t = new MyThread();&nbsp;// для наследников <strong>Thread</strong><br /><br />MyRunnable r = new MyRunnable(); Thread t = new Thread(r); //для реализации <strong>Runnable</strong><br />(объект <strong>Runnable</strong> в этом случае называется <em>target</em>). Внимание! один экземпляр Runnable можно передать в несколько потоков.<br /><br />2) запускаем:<br /><br />t.start();<br /><br />- образуется новый поток выполнения<br />- поток переходит из состояния&nbsp;<em>NEW</em> в состояние&nbsp;<em>RUNNABLE </em>и становится <em>alive</em>.<br />- когда ему предоставят возможность, поток выполнит метод <strong>run</strong>() (свой или переданного <strong>Runnable</strong>)<br />- когда метод выполнится, поток станет <em>dead (</em>состояние<em> TERMINATED)</em>.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2747,16,2,'Скомпилируется ли следующий код?<br /><br />class MyThread extends Thread {}<br />...<br />Thread t = new Thread(new MyThread());',TIMESTAMP '2019-05-22 16:57:46',TIMESTAMP '2019-05-22 16:57:46');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7322,2747,'Да. Код глупый, но валидный, т.к. класс <strong>Thread</strong> сам имплементирует <strong>Runnable</strong>',true,'');
