INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2752,16,0,'Если имеем код<br /><br />Thread one = new Thread();<br />Thread two = new Thread();<br />Thread three = new Thread();<br />one.start();<br />two.start();<br />three.start();<br /><br />то спецификация Java никак не гарантирует, что потоки выполнятся именно в таком порядке. По сути, гарантия только одна - что потоки стартуют и будут ждать, пока им дадут возможность выполниться и завершиться.',TIMESTAMP '2019-05-24 17:22:36',TIMESTAMP '2019-05-24 17:22:36');
