	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7328,2756,'Это часть JVM, определяющая, какой из потоков, имеющих состояние RUNNABLE, выполняется в данный момент времени, а какой дожидается своей очереди. Порядок выделения времени потокам не гарантирован, поэтому набор ожидающих своей очереди потоков принято называть не очередью (queue), а пулом (pool).',true,'');
