	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7329,2758,'java.lang.<strong>Thread</strong>:<br /><br />public static <em>void </em><strong>sleep</strong>(<em>long</em> millis) throws InterruptedException<br />public static <em>void </em><strong>yield</strong>()<br />public final <em>void </em><strong>join</strong>() throws InterruptedException<br />public final <em>void</em> <strong>setPriority</strong>(<em>int</em> newPriority)<br /><br />java.lang.<strong>Object</strong>:<br /><br />public final <em>void</em> <strong>wait</strong>() throws <em>InterruptedException</em><br />public final <em>void</em> <strong>notify</strong>()<br />public final <em>void</em> <strong>notifyAll</strong>()',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2759,16,2,'Опишите 5 возможных состояний потока.',TIMESTAMP '2019-05-26 11:00:59',TIMESTAMP '2019-05-26 11:10:48');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7330,2759,'Состояния потока выражаются энумом java.lang.Thread.<strong>State</strong><br /><br />* <strong>New (State.NEW). </strong>Состояние после создания экземпляра, но до вызова метода <strong>start</strong>(). Еще не считается <em>alive</em>.<br /><br />* <strong>Runnable (State.RUNNABLE).</strong> Поток (и только в таком состоянии) может быть выбран для выполнения планировщиком.<br /><br />* <strong>Running</strong> (нет соответствия в Thread.State). Состояние потока, исполняющегося в данный момент<br /><br />* <strong>Waiting/blocked/sleeping</strong> (<strong>State.WAITING, State.TIMED_WAITING, State.BLOCKED</strong>). Состояние, в котором поток не может быть передан на выполнение. Он либо заблокирован монитором/в ожидании ресурса, либо "спит" в течение определенного времени, либо ожидает выполнения другого потока. Однако поток в лбом из этих состояний все равно остается <em>alive</em>.<br /><br />* <strong>Dead</strong> (<strong>State.TERMINATED</strong>). Состояние, в которое приходит поток после отработки метода <strong>run</strong>(). Вернуться из этого состояния нельзя.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2760,16,0,'Важно! Один поток не может управлять состоянием другого! Методы <strong>sleep</strong>() или <strong>yield</strong>() - статичные, они всегда применяются к <span style="text-decoration: underline;">текущему</span> потоку, даже если вызваны на экземпляре (t.sleep()).<br /><br />Методы <strong>suspend</strong>(), <strong>resume</strong>() и <strong>stop</strong>(), которые можно вызвать на экземпляре потока - deprecated, на экзамене их нет',TIMESTAMP '2019-05-26 11:02:21',TIMESTAMP '2019-05-26 11:08:21');
