INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2763,16,0,'Несмотря на отсутствие гарантий очередности, использование Thread.<strong>sleep</strong>() - это наилучший механизм обеспечить всем созданным потокам шанс на выполнение.',TIMESTAMP '2019-05-26 13:24:21',TIMESTAMP '2019-05-26 13:24:21');
