INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (2765,16,2,'Опишите концепцию приоритета потока (<em>thread priorities</em>).',TIMESTAMP '2019-05-26 13:39:00',TIMESTAMP '2019-05-26 13:50:47');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7335,2765,'Потоки запускаются с конкретным приоритетом, обычно от 1 до 10. В большинстве JVM (но не во всех!) используется time slicing на основе приоритета. В любой момент времени, как правило, выполняется поток, приоритет которого максимальный в пуле.<br />Важно! Управлять приоритетами имеет смысл для повышения эффективности, но ни в коем случае не для бизнес-логики.<br />Итак, наличие в пуле нескольких потоков одинакового приоритета не гарантирует, что всем им будет дан шанс на выполнение.',true,'');
