	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (7835,2969,'Те, решения для которых в параллельном контексте можно реализовать легко и быстро, без особого оверхеда.<br /><br />Тревожный симптом - наличие массовых вычислений после вызова <strong>join</strong>(). В таких случаях следует тестировать производительность, и может оказаться, что последовательное вычисление будет быстрее параллельного.',true,'');
