INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (3105,17,2,'Приведите примеры "сквозной" функциональности (<em>cross-cutting concern</em>) (т.е. такой, которая оказывается задействована в разных по назначению компонентах системы) и поясните, какие проблемы она порождает с точки зрения разработки.',TIMESTAMP '2019-06-20 14:50:03',TIMESTAMP '2019-06-20 14:50:03');
	INSERT INTO Answer (id,questionId,text,correct,comment) OVERRIDING SYSTEM VALUE VALUES (8319,3105,'- логирование<br />- обработка транзакций<br />- безопасность<br /><br />Появляются два уровня сложности:<br />- code duplicate: изменения приходится вносить в нескольких местах. Даже если все вынесено в модуль и используется единый метод, все равно вызов этого места&nbsp;дуплицируется во многих местах<br />- специализированные компоненты "замусориваются" кодом, не имеющим отношения к их основной деятельности. Метод, добавляющий запись в адресную книгу, не должен беспокоиться о БД-транзакции или безопасности.',true,'');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (3106,17,0,'Иллюстрация сложности, возникающей из-за сквозной функциональности. <br /><br /><img src="uploads/32" alt="tmp6SIA4_p12.png" width="531" height="269" /><br /><br />Каждый объект не только знает о том, что логируется, транзакцируется и охраняется, но, к тому же, сам вынужден запускать для себя эти сервисы.',TIMESTAMP '2019-06-20 14:56:15',TIMESTAMP '2019-06-20 14:56:15');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (3107,17,0,'Благодаря аспектам POJO остаются POJO и могут сконцентрироваться на своих собственных задачах. AOP позволяет создавать слои функциональности, на которых покоятся бизнес-модули, ничего не зная о их присутствии.',TIMESTAMP '2019-06-20 15:31:09',TIMESTAMP '2019-06-20 15:33:33');
INSERT INTO Question (id,realmId,type,text,regDate,updateDate) OVERRIDING SYSTEM VALUE VALUES (3108,17,2,'Пусть есть класс<br /><br />package com.springinaction.knights;<br />import java.io.PrintStream;<br />public class Minstrel {<br />&nbsp;&nbsp;&nbsp; private PrintStream stream;<br />&nbsp;&nbsp;&nbsp; public Minstrel(PrintStream stream) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.stream = stream;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public void singBeforeQuest() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; stream.println("Fa la la, the knight is so brave!");<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public void singAfterQuest() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; stream.println("Tee hee hee, the brave knight " +<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; "did embark on a quest!");<br />&nbsp;&nbsp;&nbsp; }<br />}<br /><br />и попытка обеспечить его взаимодействие с классом<br /><br />package com.springinaction.knights;<br />public class BraveKnight implements Knight {<br />&nbsp;&nbsp;&nbsp; private Quest quest;<br />&nbsp;&nbsp;&nbsp; private Minstrel minstrel;<br />&nbsp;&nbsp;&nbsp; public BraveKnight(Quest quest, Minstrel minstrel) {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.quest = quest;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; this.minstrel = minstrel;<br />&nbsp;&nbsp;&nbsp; }<br />&nbsp;&nbsp;&nbsp; public void embarkOnQuest() throws QuestException {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; minstrel.singBeforeQuest();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; quest.embark();<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; minstrel.singAfterQuest();<br />&nbsp;&nbsp;&nbsp; }<br />}<br /><br />Чем плохо такое решение?',TIMESTAMP '2019-06-20 16:24:18',TIMESTAMP '2019-06-20 16:29:28');
